      objs.push(newobjarr.pop());
    }

  };

  $scope.editfun = function(val) {
    $scope.edit = true;
    $scope.show = !($scope.show);
    var newobjarr = [];
    for (i = objs.length - 1; i >= 0; i--) {
      if (val.name == objs[i].name) {
        var temp = objs.pop();
      } else {
        var tempObj = objs.pop();
        newobjarr.push(tempObj);
      }
    }
