  var callback = function(buttonIndex) {
    setTimeout(function() {
      if (buttonIndex == 1) {
        var image = document.getElementById('myImage' + valImage);
        image.src = "http://www.indre-reisid.ee/wp-content/themes/envision/lib/images/default-placeholder.png";

        switch (valImage) {
          case 1:
            imgObject.Image_1_URL = null;
            break;
          case 2:
            imgObject.Image_2_URL = null;
            break;
          case 3:
            imgObject.Image_3_URL = null;
            break;
        };

      } else if (buttonIndex == 2) {
        navigator.camera.getPicture(cameraSuccess, cameraError, {
          quality: 25,
          destinationType: Camera.DestinationType.DATA_URL
        });
      } else if (buttonIndex == 3) {
        navigator.camera.getPicture(onSuccess, onFail, {
          quality: 50,
          destinationType: Camera.DestinationType.DATA_URL,
          sourceType: Camera.PictureSourceType.SAVEDPHOTOALBUM
        });
      }
    });
  };

