  var lightBoxShown = $.jStorage.get("lightValue");
$.jStorage.set("lightValue", 0);

  $scope.nextLightBox = function() {
    var lightBoxShown = $.jStorage.get("lightValue");

    if (!lightBoxShown) {
      $("#lightBox").css({
        "right": "105px"
      });
      document.getElementById("contentLight").innerHTML = "Post An Ad!";
      $.jStorage.set("lightValue", 1);
      document.getElementById('hamMenu').click();
      return;
    }
    switch (lightBoxShown) {
      case 1:
        $("#lightBox").css({
          "right": "160px"
        });
        document.getElementById("contentLight").innerHTML = "Favorites!";
        $.jStorage.set("lightValue", 2);
        return;

      case 2:
        $("#lightBox").css({
          "right": "215px"
        });
        document.getElementById("contentLight").innerHTML = "My Ads!";
        document.getElementById("ButtonText").innerHTML = "Close";
        $.jStorage.set("lightValue", 3);
        return;

      default:
        document.getElementById('hamMenu').click();
        $.jStorage.set("lightValue", 4);

        return;

    }


  }
  $scope.noLightBox = function() {
    $.jStorage.set("lightValue", 4);
    document.getElementById('hamMenu').click();
  }

  $scope.showLightBox = function() {
    if ($.jStorage.get("lightValue") == 4) {
      return false;
    }
    return true;
  }


}

